commits.content:
body {
  margin: 0;
  font-family: sans-serif;
  background-color: #d6eaf8;
}

.container {
  display: flex;
  height: 100vh;
}

.column {
  flex: 1;
  padding: 20px;
  box-sizing: border-box;
  border-right: 1px solid #ccc;
}

.column:last-child {
  border-right: none;
}

/* Search section */
.search-box {
  display: flex;
  margin-bottom: 10px;
}

.search-box button {
  padding: 6px 12px;
  background-color: #3498db;
  color: white;
  border: none;
  cursor: pointer;
}

.results {
  list-style: none;
  padding: 0;
  margin: 10px 0;
}

.results li {
  padding: 5px 0;
  cursor: pointer;
}

.column.search-section {
  position: relative;
  height: 100vh; /* or a fixed height if needed */
  display: flex;
  flex-direction: column;
}
.search-footer {
  margin-top: auto; /* pushes footer to bottom in flex column */
  background: #fff;
  padding: 10px 16px;
  display: flex;
  justify-content: center;
  gap: 20px;
  border-top: 1px solid #ccc;
}

.search-footer button,
.search-footer i {
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
  color: #444;
}

.search-footer i:hover,
.search-footer button:hover i,
.search-footer .active {
  color: #007bff;
}



/* Result section */
.result {
  display: flex;
  justify-content: center;
  align-items: center;
}

.result img {
  max-width: 100%;
  max-height: 300px;
  border: 2px solid #000;
}

/* Recent searches */
.recent h3 {
  margin-bottom: 10px;
}

.recent ul {
  list-style: none;
  padding: 0;
}

.recent li {
  padding: 5px 0;
  cursor: pointer;
}

.results.list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.results.list .result-item {
  display: flex;
  align-items: center;
  margin-bottom: 10px;
}

.results.tile {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.tile-row {
  display: flex;
  justify-content: space-between;
  gap: 20px;
}

.tile-item {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.search-box input {
  width: 50%;
  padding: 10px 14px;
  font-size: 16px;
  border: 2px solid #007bff;
  border-radius: 8px;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}

.search-box input:focus {
  border-color: #0056b3;
  box-shadow: 0 0 0 2px rgba(0, 123, 255, 0.25);
}

.search-box input::placeholder {
  color: #999;
}

.artist-info {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.artist-image {
  max-width: 100%;
  height: auto;
  border-radius: 8px;
  margin-bottom: 16px;
}

iframe {
  border: none;
  width: 100%;
  max-width: 400px;
  height: 120px;
}
@media (max-width: 768px) {
  .container {
    flex-direction: column;
    height: auto;
  }

  .column {
    border-right: none;
    border-bottom: 1px solid #ccc;
    padding: 16px;
  }

  .column:last-child {
    border-bottom: none;
  }

  .search-box input {
    width: 100%; /* כל הרוחב במובייל */
  }

  .tile-row {
    flex-direction: column;
    gap: 12px;
  }

  .tile-item {
    width: 100%;
  }

  iframe {
    max-width: 100%;
    height: auto;
  }
}
