commits.content:
@media (max-width: 768px) {
  .container {
    flex-direction: column;
    height: auto;
  }

  .column {
    border-right: none;
    border-bottom: 1px solid #ccc;
    padding: 16px;
  }

  .column:last-child {
    border-bottom: none;
  }

  .search-box input {
    width: 100%;
  }

  .tile-row {
    flex-direction: column;
    gap: 12px;
  }

  .tile-item {
    width: 100%;
  }

  iframe {
    max-width: 100%;
    height: auto;
  }
}
