.search-box {
  display: flex;
  margin-bottom: 10px;
}

.search-box input {
  width: 50%;
  padding: 10px 14px;
  font-size: 16px;
  border: 2px solid #007bff;
  border-radius: 8px;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}

.search-box input:focus {
  border-color: #0056b3;
  box-shadow: 0 0 0 2px rgba(0, 123, 255, 0.25);
}

.search-box input::placeholder {
  color: #999;
}

.search-box button {
  padding: 6px 12px;
  background-color: #3498db;
  color: white;
  border: none;
  cursor: pointer;
}

.column.search-section {
  position: relative;
  height: 100vh;
  display: flex;
  flex-direction: column;
}

.search-footer {
  margin-top: auto;
  background: #fff;
  padding: 10px 16px;
  display: flex;
  justify-content: center;
  gap: 20px;
  border-top: 1px solid #ccc;
}

.search-footer button,
.search-footer i {
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
  color: #444;
}

.search-footer i:hover,
.search-footer button:hover i,
.search-footer .active {
  color: #007bff;
}
